using FireSharp;
using FireSharp.Config;
using FireSharp.Interfaces;
using FireSharp.Response;
using System;
using System.Collections.Generic;
using System.Linq;
using WebApplication1.Models.DTO;

namespace WebApplication1.Service
{
    public class Firebase
    {
        static IFirebaseConfig config = new FirebaseConfig { BasePath = "https://cntt3-79ca3-default-rtdb.firebaseio.com" };
        static IFirebaseClient client = new FirebaseClient(config);

        public bool AddImageUser(ImageUser image)
        {
            try
            {
                image.Avatar = "";
                client.Push("/images",image);
                return true;
            }
            catch { return false; }
        }

        public ImageUser GetImageDetails(int ID)
        {
            FirebaseResponse response = client.Get("images/"+GetKeyByCode(ID));
            ImageUser image = response.ResultAs<ImageUser>();
            return image;
        }
        private String GetKeyByCode(int ID)
        {
            FirebaseResponse response = client.Get("/images");
            Dictionary<String, ImageUser> dictclothes = response.ResultAs<Dictionary<String, ImageUser>>();
            String key = dictclothes.FirstOrDefault(x => x.Value.Id == ID).Key;
            return key;
        }


        public RealtimeData GetFireBase()
        {
            FirebaseResponse response = client.Get("RealtimeData");
            RealtimeData data = response.ResultAs<RealtimeData>();
           
            return data;
        }
        public HistoryData GetHistoryFireBase()
        {
            FirebaseResponse response = client.Get("HistoryDataDHT");
            HistoryData data = response.ResultAs<HistoryData>();
            return data;
        }
    }
}
